definition.script:
pipeline {
    agent any
    tools{
        maven 'maven-3.9.6'
        jdk 'jdk 21'
    }

    stages {
        stage('Git-Checkout') {
            steps {
                git changelog: false, poll: false, url: 'https://github.com/ranikote/addressbook.git'
            }
        }
        stage('build') {
            steps {
                sh 'mvn compile'
            }
        }
        stage('Install') {
            steps {
                sh 'mvn clean install'
            }
        }
        stage('pacakge') {
            steps {
                sh 'mvn package'
            }
        }
    }
}
