definition.script:
pipeline {
    agent any

    tools {
        maven 'maven-3.9.6'
        jdk 'jdk-17'
    }

    parameters {
        string(
            name: 'BRANCH_NAME',
            defaultValue: 'main',
            description: 'Git branch to build'
        )

        booleanParam(
            name: 'RUN_TESTS',
            defaultValue: true,
            description: 'Run unit tests'
        )

        choice(
            name: 'BUILD_TYPE',
            choices: ['debug', 'release'],
            description: 'Choose the build type'
        )
    }

    stages {

        stage('Checkout Code') {
            steps {
                git branch: "${params.BRANCH_NAME}",
                    changelog: false,
                    poll: false,
                    url: 'https://github.com/ranikote/Boardgame.git'
            }
        }

        stage('Maven Compile') {
            steps {
                sh 'mvn clean compile'
            }
        }

        stage('Test') {
            when {
                expression { params.RUN_TESTS }
            }
            steps {
                echo "Running tests..."
                sh 'mvn test'
            }
        }

        stage('Build') {
            steps {
                echo "Build type selected: ${params.BUILD_TYPE}"
                sh "mvn package -P${params.BUILD_TYPE}"
            }
        }
    }
}
